function removeBotaoAtivo(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].classList.remove("episodes__button--active")}function escondeEpisodio(){var e=document.querySelectorAll("[data-tab-id]");for(let t=0;t<e.length;t++)e[t].classList.remove("episodes__content__list__item--active")}function abreFechaResposta(t){t.target.parentNode.classList.toggle("faq__questions__item--is-open")}document.addEventListener("DOMContentLoaded",function(){var e=document.querySelectorAll("[data-tab-button]"),o=document.querySelectorAll("[data-faq-question]");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(t){t.target.dataset.tabButton,document.querySelector});for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(t){var e=t.target.dataset.tabButton,e=document.querySelector(`[data-tab-id=${e}]`);removeBotaoAtivo(),t.target.classList.add("episodes__button--active"),escondeEpisodio(),e.classList.add("episodes__content__list__item--active")});for(let t=0;t<o.length;t++)o[t].addEventListener("click",abreFechaResposta)});